mapper.select:
SELECT SENDERID, MESSAGEID, SORTID, CHANNELID
FROM CHATCHECKLASTMSG
WHERE CHANNELID = #{channelId} AND LOGINID = #{loginId}

SELECT totalUnread
FROM unread
WHERE LOGINID = #{loginId};

SELECT USERID, PROFILEIMAGE
FROM USER
WHERE LOGINID = #{loginId};

SELECT CREATEDAT
FROM exitchatroomtime
WHERE CHANNELID = #{channelId} AND LOGINID = #{loginId}
