mapper.select:
SELECT * FROM mileageHistory
WHERE userId = #{userId}
ORDER BY createdAt DESC

mapper.update:
UPDATE mileage 
SET mileage = mileage - #{points} 
WHERE userId = #{userId} AND mileage >= #{points}

mapper.select:
SELECT IFNULL(SUM(mileagePoints), 0) 
FROM mileageHistory 
WHERE userId = #{userId}
