mapper.select:
SELECT userId, loginId,password, userName, userNickname, email, birthday, phoneNum
FROM user
where loginId =#{loginId} and password = #{password}

mapper.update:
UPDATE user
SET

   email =#{email},
   birthday = #{birthday},
   phoneNum= #{phoneNum}

WHERE  loginId =#{loginId}

